local OS_ICON_VOICE = ""
local OS_ICON_WEDGE_CLOSE = ""
local OS_ICON_WEDGE_BACK = ""
local OS_ICON_BUBBLE_PROMPT_LEFT = ""

wedge.mediaPlayers = {}

