function event(args)
    print("lua event :: " .. Native:tableToJson(args))
    if (args == nil) then
        return
    end
    if (wedge.currentAdData.materialType == AD_TYPE_IMAGE and args.eventType == tostring(1)) then
        -- resume
        wedge.mediaPlayPaused = false
    end
